import React, {Component} from 'react'

class Prices extends Component{
    state = {
        currency: 'USD'
    }

    usdRate(rate){
        var newString = rate.replace(",", "");
        return parseInt(newString).toFixed(2)
    }

    render(){
        return (
            <div>
                <ul>
                    <li>Currency: {this.props.bpi.USD.description}</li>
                    <li>Rate: {this.usdRate(this.props.bpi.USD.rate)}</li>
                    <li>Symbol: {this.props.bpi.USD.symbol}</li>
                </ul>
            </div>
        )
    }
}

export default Prices
